jQuery(document).ready(function($) {

	jQuery('.swf_pagination li a').live('click', function() {
		if (!jQuery(this).parent().hasClass('swf-active')) {
			jQuery('.swf_pagination li').removeClass('swf-active');
			jQuery('.swf_content li').removeClass('swf-active');
			jQuery(this).parent().addClass('swf-active');
			jQuery(jQuery(this).attr('href')).addClass('swf-active');
		}

		return false;
	});

	var fontBase = 20;

	jQuery('#swf-smaller').click(function() {
		if (fontBase > 12) {
			fontBase = fontBase - 2;
			jQuery('.swf-font-preview').css('font-size',fontBase+'px');
		}
		return false;
	});

	jQuery('#swf-larger').click(function() {
		if (fontBase < 40) {
			fontBase = fontBase + 2;
			jQuery('.swf-font-preview').css('font-size',fontBase+'px');
		}
		return false;
	});

});